	insert.Connection = mySqlConnection;
	;
	insert.CommandText = "INSERT INTO MEU_cupom (MEU_cod_ecf, " +
		"MEU_cod_cliente, MEU_numero_cupom, MEU_acrescimo, MEU_desconto, MEU_data, MEU_status) " +
		"VALUES (@cod_ecf, @cod_cliente, @numero_cupom, @acrescimo, @desconto, @data, @status) ";
 insert.CommandType = CommandType.Text;
	//populando os parametros
	insert.Parameters.Add("@cod_ecf",SqlDbType.Int,4,"MEU_cod_cupom");
	insert.Parameters.Add("@cod_cliente",SqlDbType.Int,4,"MEU_cod_cliente");
	insert.Parameters.Add("@numero_cupom",SqlDbType.Int,4,"MEU_numero_cupom");
	insert.Parameters.Add("@acrescimo",SqlDbType.SmallMoney,8,"MEU_acrescimo");
	insert.Parameters.Add("@desconto",SqlDbType.SmallMoney,8,"MEU_desconto");
 insert.Parameters.Add("@data",SqlDbType.SmallDateTime,8,"MEU_data");
 insert.Parameters.Add("@status",SqlDbType.Char,2,"MEU_status");

 //criando o comando de select
 SqlCommand select = new SqlCommand();
 select.Connection = mySqlConnection;
 select.CommandType = CommandType.Text;
 select.CommandText = "SELECT MEU_cod_cupom FROM MEU_cupom " + 
 	", MEU_ecf WHERE MEU_ecf.MEU_cod_ecf = MEU_cupom.MEU_cod_ecf " +
 	"AND MEU_ecf.MEU_numero_serie = @numero_serie " + 
 	"AND MEU_cupom.MEU_numero_cupom = @numero_cupom  AND MEU_cupom.MEU_data = @data ";

 //populando os parametros
 select.Parameters.Add("@numero_serie",SqlDbType.Char,22,"MEU_ecf.MEU_numero_serie");
 select.Parameters.Add("@numero_cupom",SqlDbType.Int,4,"MEU_cupom.MEU_numero_cupom");
 select.Parameters.Add("@data",SqlDbType.SmallDateTime,8,"MEU_cupom.MEU_data");

 //crindo o comando de update 
 SqlCommand update = new SqlCommand();
 update.Connection = mySqlConnection;
 update.CommandType = CommandType.Text;
 update.CommandText = "UPDATE MEU_cupom SET MEU_cupom.MEU_status = 'CA' " +
 	"WHERE MEU_cupom.MEU_numero_cupom = @numero_cupom" + 
 	" AND MEU_cupom.MEU_data = @data"+
 	" AND MEU_cupom.MEU_cod_ecf = (SELECT MEU_ecf.MEU_cod_ecf FROM MEU_ecf " +
 	" WHERE MEU_ecf.MEU_numero_serie = @numero_serie )";

 //populando os parametros
 update.Parameters.Add("@numero_cupom",SqlDbType.Int,4,"MEU_cupom.MEU_numero_cupom");
 update.Parameters.Add("@data",SqlDbType.SmallDateTime,8,"MEU_cupom.MEU_data");
 update.Parameters.Add("@numero_serie",SqlDbType.Char,22,"MEU_ecf.MEU_numero_serie");

 ...
